import React from 'react';
import {Row, Col, Container} from 'react-grid-system';
import {work} from '../components/work';
import {spacer1, spacer2, spacer3, spacer4} from '../components/constants';
import Tilt from 'react-tilt';


const workList= work.map(tile=>(
    <Col md={6}>
        <Tilt>

                <div style={{backgroundImage: "../images/Facebook.png"}}>

                </div>
                <a href="#">
                <img src={require(`../images/workImages/${tile.imgLink}.png`)} alt={tile.company} />
                </a>

                <h3 style={{marginTop: spacer2}}> {tile.position} </h3>
                <h2 style={{marginTop: spacer1}}> {tile.company} </h2>
        </Tilt>
    </Col>
));

class Home extends React.Component{
    render(){
        return(
            <div>
                <Container>
                    <Row style={{height: "50vw"}}>
                        <Col md={8}>
                            <h1>
                                I'm <span className="stroke">Paridhi Khaitan</span>
                            </h1>
                            <h1> 
                                I <span className="stroke">develop</span> websites and mobile 
                                apps. I also enjoy <span className="stroke">design!</span>

                            </h1>
                        </Col>
                    </Row>
                    <Row justify="center">
                        {workList}
                    </Row>
                </Container>
            </div>
        );
    }
}

export default Home;
